mapper.select:
SELECT emp_id AS empId, emp_team AS empTeamCode, T.team_name AS empTeamName,
emp_grade AS empGradeCode, G.grade_name AS empGradeName,
emp_num AS empNum, emp_name AS empName, emp_join AS empJoin, emp_Date AS empDate, emp_confirm AS empConfirm, emp_auth AS empAuth FROM grp_employee E, grp_team T, grp_grade G WHERE E.emp_team = T.team_id AND E.emp_grade = G.grade_id ORDER BY emp_id DESC LIMIT #{start}, #{end}

SELECT emp_id AS empId, emp_team AS empTeamCode, T.team_name AS empTeamName,
emp_grade AS empGradeCode, G.grade_name AS empGradeName,
emp_num AS empNum, emp_name AS empName, emp_join AS empJoin FROM grp_employee E, grp_team T, grp_grade G WHERE E.emp_team = T.team_id AND E.emp_grade = G.grade_id AND emp_num = #{sessionNum}
